// src/components/MostCommonIngredients.js
import React, { useState } from 'react';

function MostCommonIngredients() {
  const [ingredients, setIngredients] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchIngredients = () => {
    setLoading(true);
    fetch('http://127.0.0.1:8000/api/common-ingredients/')
      .then(response => response.json())
      .then(data => {
        setIngredients(data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching common ingredients:', error);
        setLoading(false);
      });
  };

  return (
    <div>
      <h2>Most Common Ingredients</h2>
      <button onClick={fetchIngredients}>Fetch Ingredients</button>
      {loading && <p>Loading...</p>}
      <ul>
        {ingredients.map((item, index) => (
          <li key={index}>{item.ingredients} (Count: {item.count})</li>
        ))}
      </ul>
    </div>
  );
}

export default MostCommonIngredients;
