import React, { useState } from 'react';
import axios from 'axios';

const GetRecipesByCuisine = () => {
    const [cuisine, setCuisine] = useState('');
    const [recipes, setRecipes] = useState([]);

    const fetchRecipes = () => {
        axios.get(`http://127.0.0.1:8000/api/cuisine/${cuisine}/`)
            .then(response => setRecipes(response.data))
            .catch(error => console.error('Error fetching recipes by cuisine:', error));
    };

    return (
        <div>
            <h2>Get Recipes by Cuisine</h2>
            <input
                type="text"
                value={cuisine}
                onChange={(e) => setCuisine(e.target.value)}
                placeholder="Enter cuisine"
            />
            <button onClick={fetchRecipes}>Fetch Recipes</button>
            <ul>
                {recipes.map(recipe => (
                    <li key={recipe.id}>{recipe.name}</li>
                ))}
            </ul>
        </div>
    );
};

export default GetRecipesByCuisine;
