// src/components/AddRecipe.js
import React, { useState } from 'react';

function AddRecipe() {
  const [name, setName] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const [cuisine, setCuisine] = useState('');
  const [vegetarian, setVegetarian] = useState(false);
  const [rating, setRating] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://127.0.0.1:8000/api/add-recipe/', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name,
        ingredients,
        instructions,
        cuisine,
        vegetarian,
        rating,
      }),
    })
      .then(response => response.json())
      .then(data => {
        alert('Recipe added successfully!');
        setName('');
        setIngredients('');
        setInstructions('');
        setCuisine('');
        setVegetarian(false);
        setRating('');
      })
      .catch(error => {
        console.error('Error adding recipe:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add a New Recipe</h2>
      <label>
        Name:
        <input type="text" value={name} onChange={e => setName(e.target.value)} required />
      </label>
      <label>
        Ingredients:
        <textarea value={ingredients} onChange={e => setIngredients(e.target.value)} required />
      </label>
      <label>
        Instructions:
        <textarea value={instructions} onChange={e => setInstructions(e.target.value)} required />
      </label>
      <label>
        Cuisine:
        <input type="text" value={cuisine} onChange={e => setCuisine(e.target.value)} />
      </label>
      <label>
        Vegetarian:
        <input type="checkbox" checked={vegetarian} onChange={e => setVegetarian(e.target.checked)} />
      </label>
      <label>
        Rating (optional):
        <input type="number" value={rating} onChange={e => setRating(e.target.value)} min="1" max="5" />
      </label>
      <button type="submit">Add Recipe</button>
    </form>
  );
}

export default AddRecipe;
