import axios from 'axios';

const API_BASE_URL = 'http://127.0.0.1:8000/api';

export const getVegetarianRecipes = () => axios.get(`${API_BASE_URL}/vegetarian/`);
export const getRecipesByCuisine = (cuisine) => axios.get(`${API_BASE_URL}/cuisine/${cuisine}/`);
export const getCommonIngredients = () => axios.get(`${API_BASE_URL}/common-ingredients/`);
// export const addRecipeWithRating = (recipe) => axios.post(`${API_BASE_URL}/add-recipe/`, recipe);

export const addRecipeWithRating = (recipe) => {
  return axios.post(`${API_BASE_URL}/add-recipe/`, recipe)
    .catch(error => {
      if (error.response) {
        // Server responded with a status other than 2xx
        console.error('Error response:', error.response);
        console.error('Error status:', error.response.status);
        console.error('Error data:', error.response.data);
      } else if (error.request) {
        // Request was made but no response received
        console.error('Error request:', error.request);
      } else {
        // Something happened in setting up the request
        console.error('Error message:', error.message);
      }
      throw error;
    });
};
