import React, { useState } from 'react';
import { getRecipesByCuisine } from '../services/api';

function RecipeByCuisine() {
  const [cuisine, setCuisine] = useState('');
  const [recipes, setRecipes] = useState([]);

  const fetchRecipes = () => {
    getRecipesByCuisine(cuisine)
      .then(response => setRecipes(response.data))
      .catch(error => console.error('Error fetching recipes by cuisine:', error));
  };

  return (
    <div>
      <h2>Get Recipes by Cuisine</h2>
      <input
        type="text"
        value={cuisine}
        onChange={e => setCuisine(e.target.value)}
        placeholder="Enter cuisine"
      />
      <button onClick={fetchRecipes}>Get Recipes</button>
      <ul>
        {recipes.map(recipe => (
          <li key={recipe.id}>
            <h3>{recipe.name}</h3>
            <p>{recipe.ingredients}</p>
            <p>{recipe.instructions}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default RecipeByCuisine;
