import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

const RecipesByCuisine = () => {
  const [cuisine, setCuisine] = useState('');
  const [recipes, setRecipes] = useState([]);

  const fetchRecipes = useCallback(async () => {
    try {
      const response = await axios.get(`http://127.0.0.1:8000//api/cuisine/${cuisine}/`);
      setRecipes(response.data);
    } catch (error) {
      alert('Error fetching recipes');
    }
  }, [cuisine]);

  useEffect(() => {
    if (cuisine) {
      fetchRecipes();
    }
  }, [cuisine, fetchRecipes]);

  return (
    <div>
      <h2>Recipes by Cuisine</h2>
      <input type="text" placeholder="Cuisine" value={cuisine} onChange={(e) => setCuisine(e.target.value)} />
      <ul>
        {recipes.map((recipe) => (
          <li key={recipe.id}>{recipe.name}</li>
        ))}
      </ul>
    </div>
  );
};

export default RecipesByCuisine;
