import React, { useState } from 'react';
import axios from 'axios';

const RandomRecipe = () => {
  const [recipe, setRecipe] = useState(null);

  const fetchRandomRecipe = async () => {
    try {
      const response = await axios.get('http://http://127.0.0.1:8000/api/random-recipe/');
      setRecipe(response.data);
    } catch (error) {
      alert('Error fetching random recipe');
    }
  };

  return (
    <div>
      <h2>Random Recipe</h2>
      <button onClick={fetchRandomRecipe}>Get Random Recipe</button>
      {recipe && (
        <div>
          <h3>{recipe.name}</h3>
          <p>{recipe.ingredients}</p>
          <p>{recipe.instructions}</p>
          <p>Cuisine: {recipe.cuisine}</p>
          <p>Vegetarian: {recipe.vegetarian ? 'Yes' : 'No'}</p>
        </div>
      )}
    </div>
  );
};

export default RandomRecipe;
