import React, { useState } from 'react';
import axios from 'axios';

const AddRecipeForm = () => {
  const [name, setName] = useState('');
  const [ingredients, setIngredients] = useState('');
  const [instructions, setInstructions] = useState('');
  const [cuisine, setCuisine] = useState('');
  const [vegetarian, setVegetarian] = useState(false);
  const [rating, setRating] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const recipe = { name, ingredients, instructions, cuisine, vegetarian, rating: parseInt(rating) };
    try {
      await axios.post('http://<your-ec2-public-ip>/api/add-recipe/', recipe);
      alert('Recipe added successfully!');
    } catch (error) {
      alert('Error adding recipe');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Add a Recipe</h2>
      <input type="text" placeholder="Name" value={name} onChange={(e) => setName(e.target.value)} required />
      <textarea placeholder="Ingredients" value={ingredients} onChange={(e) => setIngredients(e.target.value)} required />
      <textarea placeholder="Instructions" value={instructions} onChange={(e) => setInstructions(e.target.value)} required />
      <input type="text" placeholder="Cuisine" value={cuisine} onChange={(e) => setCuisine(e.target.value)} />
      <label>
        <input type="checkbox" checked={vegetarian} onChange={(e) => setVegetarian(e.target.checked)} />
        Vegetarian
      </label>
      <input type="number" placeholder="Rating" value={rating} onChange={(e) => setRating(e.target.value)} required />
      <button type="submit">Add Recipe</button>
    </form>
  );
};

export default AddRecipeForm;
