import React, { useState } from 'react';
import axios from 'axios';

const AddRecipeWithRating = () => {
    const [recipe, setRecipe] = useState({
        name: '',
        ingredients: '',
        instructions: '',
        cuisine: '',
        vegetarian: false,
        rating: 0
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setRecipe(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleCheckboxChange = () => {
        setRecipe(prevState => ({
            ...prevState,
            vegetarian: !prevState.vegetarian
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://127.0.0.1:8000/api/add-recipe/', recipe)
            .then(response => console.log('Recipe added:', response.data))
            .catch(error => console.error('Error adding recipe:', error));
    };

    return (
        <div>
            <h2>Add Recipe with Rating</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="name"
                    value={recipe.name}
                    onChange={handleChange}
                    placeholder="Recipe Name"
                />
                <textarea
                    name="ingredients"
                    value={recipe.ingredients}
                    onChange={handleChange}
                    placeholder="Ingredients"
                ></textarea>
                <textarea
                    name="instructions"
                    value={recipe.instructions}
                    onChange={handleChange}
                    placeholder="Instructions"
                ></textarea>
                <input
                    type="text"
                    name="cuisine"
                    value={recipe.cuisine}
                    onChange={handleChange}
                    placeholder="Cuisine"
                />
                <label>
                    Vegetarian:
                    <input
                        type="checkbox"
                        checked={recipe.vegetarian}
                        onChange={handleCheckboxChange}
                    />
                </label>
                <input
                    type="number"
                    name="rating"
                    value={recipe.rating}
                    onChange={handleChange}
                    placeholder="Rating"
                    min="0"
                    max="5"
                />
                <button type="submit">Add Recipe</button>
            </form>
        </div>
    );
};

export default AddRecipeWithRating;
